#define t float(iFrame)
#define PI 3.14159

float noiseSample(vec2 uv){
    return length(texture(iChannel1, uv).rgb);
}

float fbm(vec2 uv){
    uv.y += t*.001;
    float sum = 0.;
    float freq = 0.05;
    float amp = 1.;
    for(int i = 0; i < 4; i++){
        sum += noiseSample(uv*freq)*amp;
        freq *= 2.5;
        amp *= .5;
    }
	return sum;
}

vec2 wind(vec2 uv, float mag, float startAngle, float angleRange){
    float windNoise = fbm(uv);
    float windAngle = startAngle+angleRange*windNoise;
    vec2 windOffset = vec2(cos(windAngle), sin(windAngle))*mag;
    return windOffset;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 cv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;
    float mag = smoothstep(0.4, 0.1, length(uv-.5));
    vec2 move = wind(uv, .001*mag, -PI*.5, PI);
    vec3 img = texture(iChannel1, uv).rgb;
    vec3 old = texture(iChannel0, uv+move).rgb;
    vec3 new = mix(img, old, .995);
    fragColor = vec4(new, 1);
}
