void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy-0.5;

    uv.x *= iResolution.x/iResolution.y;
    if (uv.x > 0.0) uv.y = -uv.y;

    float r = 0.5*cos(2.0*atan(uv.y, uv.x));
    float d = dot(uv, uv);
    float s = 0.003/sqrt(d);
    float c = 1.0 - smoothstep(r, r+s, d)
                  - smoothstep(r, r-s, d);

    fragColor = vec4(c, c, c, 1.0);

    vec3 power = vec3(0.5, 0.2, 0.33);
    for (float i = 0.0; i < 1.0; i += 0.05) {
        float t = 1.0 + iTime*1.5 + i;
        vec2 uv2 = uv + vec2(cos(t), sin(t)) * sqrt(r);
        float size = (0.0001+i/25.0)/pow(d, 0.2);
        vec3 color = vec3(sin(t*4.0), cos(t*3.0), sin(t*7.0));
        c = smoothstep(size, 0.0, sqrt(dot(uv2, uv2)));
        fragColor += vec4(power * (1.0 + color) * c, 1.);   
    }
}
