vec2 curve(vec2 uv, float bucket){
     uv = (uv - 0.5) * 2.0;
     uv *= 1.08;
     uv.x *= 1.0 + pow(abs(uv.y) * (1.0 - (bucket * 0.6 + 0.4)), 2.0);
     uv.y *= 1.0 + pow(abs(uv.x) * (1.0 - (bucket * 0.6 + 0.4)), 2.0);
     uv = (uv / 2.0) + 0.5;
     uv =  uv *0.92 + 0.04;
     return uv;
 }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float speed = 0.0;
   float focusScale = 0.02;
   float colorDepart = 0.1;

   const float PI = 3.1415926;
    vec2 textureCoordinate = fragCoord/iResolution.xy;
     float timeWithSpeed = iTime * (2.0 * speed);
     float fTime = fract(timeWithSpeed*12.0/ (PI * 2.0)) * (PI * 2.0);
     float fTimeStep = step(fTime,0.0);
     fTime = (1.0 - fTimeStep) * fTime;
     float scale = 1.0 + focusScale * max( sin(fTime) , 0.0 );
     float scalergb = 1.0 + colorDepart * max( sin(fTime + PI/2.0) , 0.0 );
     vec2 newTextureCoordinate = vec2((scale - 1.0) * 0.5 + textureCoordinate.x / scale, (scale - 1.0) * 0.5 + textureCoordinate.y / scale);
     vec4 textureColor = texture(iChannel0, newTextureCoordinate);
     vec4 shiftColor1 = texture(iChannel0, newTextureCoordinate + vec2(-0.05 * (scalergb - 1.0), -0.05 * (scalergb - 1.0)));
     vec4 shiftColor2 = texture(iChannel0, newTextureCoordinate + vec2(-0.1 * (scalergb - 1.0), -0.1 * (scalergb - 1.0)));
     vec3 blendFirstColor = vec3(textureColor.r, textureColor.g, shiftColor1.b);
     vec3 blend3DColor = vec3(shiftColor2.r, blendFirstColor.g, blendFirstColor.b);
     fragColor = vec4(blend3DColor, textureColor.a);
 }
