void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(mix(vec3(0.80, 0.78, 0.75), vec3(0.027, 0.239, 0.411), texture(iChannel0, uv).r), 1.0);
}

#define BRUSH_SIZE 20.0
#define STROKE_LENGTH 600.0

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 uv = fragCoord/iResolution.xy;   
    vec4 brushData = texture(iChannel0, vec2(0.0));
    float brushStrength = brushData.z;

    vec3 col = texture(iChannel0, uv).rgb; 
	vec2 dirVec = normalize(iMouse.xy -  brushData.xy);
	dirVec = vec2(dirVec.y, -dirVec.x);
	float vd = dot(fragCoord, dirVec);

	float brushStregthRamp = clamp(sin(brushStrength*3.1415926), 0.0, 1.0);

    for(int i=0; i < 20; i++) {
        vec2 mPos = mix(iMouse.xy, brushData.xy, float(i)/20.0);
    	float d = 1.0 - smoothstep(0.0, 1.0, distance(fragCoord, mPos) / BRUSH_SIZE / brushStregthRamp);
        d *= smoothstep(0.2, 1.0, (noise(fragCoord-mPos)+0.75)/5.0) * 10.0;               
        float bd = 1.0 - smoothstep(0.0, 1.0, distance(fragCoord, mPos) / BRUSH_SIZE / brushStregthRamp / 1.5);
        bd *= smoothstep(0.1, 1.0, (noise(vec2(brushStrength * 20.0, 0.0) + (fragCoord-mPos)/2.0)+0.0)/5.0) * 10.0;               
        col += vec3(bd) + vec3(d);
    }

    brushStrength -= distance(iMouse.xy, brushData.xy) / STROKE_LENGTH;
    if(iMouse.z != brushData.w) {
        if(iMouse.w > 0.0) {
    		brushStrength = 1.0;
        } else {
            brushStrength = 0.0;
        }
    }
    brushStrength = clamp(brushStrength, 0.0, 1.0);
    vec4 newBrushData = vec4(iMouse.xy, brushStrength, iMouse.z);
    fragColor = mix(newBrushData, vec4(col, 1.0), smoothstep(0.99, 1.0, fragCoord.x));
}
