// cell count
float count = 16.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    vec2 cell = floor(uv * count) / count;

    // cell index
    float index = cell.x * cell.y;

    float shade = index;

    shade = abs(sin(iTime + cell.x + index));

    // Output to screen
    fragColor = vec4(vec3(shade),1.0);
}
