const float pi = 3.1415926;

vec3 rgb_intensity = vec3(1.0); 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    if ((iMouse.y < iResolution.y) && (iMouse.y > 0.95 * iResolution.y)) {
        rgb_intensity.r = iMouse.x / iResolution.x;
    } else if ((iMouse.y < 0.95 * iResolution.y) && (iMouse.y > 0.90 * iResolution.y)) {
        rgb_intensity.g = iMouse.x / iResolution.x;
    } else if ((iMouse.y < 0.90 * iResolution.y) && (iMouse.y > 0.85 * iResolution.y)) {
        rgb_intensity.b = iMouse.x / iResolution.x;
    }

    vec3 color = texture(iChannel0, uv).rgb;    

    if (uv.y > 0.95) {
	  	fragColor = (fragCoord.x < iMouse.x) ? vec4(1.0, 0.0, 0.0, 1.0) : vec4(0.25, 0.0, 0.0, 1.0);
        return;
    } else if (uv.y > 0.90) {
	  	fragColor = (fragCoord.x < iMouse.x) ? vec4(0.0, 1.0, 0.0, 1.0) : vec4(0.0, 0.25, 0.0, 1.0);
        return;
    } else if (uv.y > 0.85) {
	  	fragColor = (fragCoord.x < iMouse.x) ? vec4(0.0, 0.0, 1.0, 1.0) : vec4(0.0, 0.0, 0.25, 1.0);
        return;
    }
    fragColor = vec4(pow(color, rgb_intensity), 1.0f); 
}
