float hash(float n) { return fract(sin(n) * 1e4); }
float hash(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }


float noise(float x) {
	float i = floor(x);
	float f = fract(x);
	float u = f * f * (3.0 - 2.0 * f);
	return mix(hash(i), hash(i + 1.0), u);
}

vec4 checker( vec2 p ) 
{    
    return vec4(vec3(fract( p.x*2. ) >= .5 == fract( p.y*2. ) > .5 ? 0. : 1.),1.); 
}

vec2 sinc(vec2 c) {
	return vec2(sin(c.x) * cosh(c.y), cos(c.x) * sinh(c.y));
}

vec2 cosc(vec2 c) {
 	return vec2(cos(c.x) * cosh(c.y), -sin(c.x) * sinh(c.y));
}

vec2 tanc(vec2 c) {
 	vec2 c2 = 2.0 * c;
 	return vec2(sin(c2.x), sinh(c2.y))/(cos(c2.x) + cosh(c2.y));
}

vec2 f(vec2 z) {
	return tanc(sinc(cosc(z)));
}

float boca(float x)
{
    return 0.5+0.5*cos(x);
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*.5) / iResolution.y;


    vec3 col = vec3(1.);

    // Output to screen
    fragColor = vec4(col, 1.0);

    if(fragColor.x>.5) fragColor= checker(uv);

    	vec2 z = (uv+.1)*6.1;

	z = f(z);

	float l = length(floor(z + 0.5) - z);

	vec3 color = vec3(0.0,0.0,0.0);

	if (l < 0.1) {
		l *= 40.0*noise(iTime/3.);
		color.xyz = vec3(sin(l * 7.0 + 1.35));
		color.xyz += cos(l * 5.0 + 0.5);
		color.xyz += sin(l * 7.0 + 1.43);
	}

    if(uv.y > .0 || uv.y < -.2) color = vec3(0.);


	fragColor += vec4( (1.-fragColor.x)*(color), 1.0 );

        vec3 color2 = vec3(0.0,0.0,0.0);
    if(!(uv.y > (-.15+min((sin(((uv.x/10.)*iTime)+mod(uv.x,.125))*.1),-.01)) || uv.y < -.2)) color2 = vec3(1.);
    fragColor += vec4( color2, 1.0 );
}
