void mainImage( out vec4 f, in vec2 g )
{
    vec2 uv = g/  iResolution.xy;
    uv.y=abs(-2.0*((1.0-uv.y)-0.5)); // fun

    f = texture(iChannel0, uv); 
    f.x*=0.35;
    f = vec4( sin(f.x*3.141*2.5), sin(f.x*3.141*2.0),sin(f.x*3.141*1.0),1.0);
}

void mainImage( out vec4 f, in vec2 g )
{
    vec2 s = iResolution.xy;
    vec2 uv = g/s;
    vec4 v = texture(iChannel0, vec2(uv.x,0.29));  
    float a = uv.y*uv.y;
