{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv += .5;

    //uv.x /=-1.0+cos(iTime)*3.;

    // Time varying pixel color
    float left = step(sin(uv.x*20.+cos(iTime))+5.+sin(iTime),uv.y*20.+cos(iTime/3.));
    float left1 = step(sin((uv.x-sin(iTime))*sin(iTime/5.)*20.)+10.+cos(iTime+10.), (uv.y)*20.);
    float left2 = step(sin(uv.x*30.+cos(iTime))+15.+sin(iTime/uv.y)*cos(iTime/10.),uv.y*20.+cos(iTime/3.));
    float c = left - left1;


    fragColor = vec4(left2,left1,(left2+left)*0.9,1.0);
}
