void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//int b=getBufferByTime(iTime);
	int b=getBufferByFrame(iFrame);

    if (b==0) fragColor = texelFetch(iChannel0,ivec2(fragCoord),0);
    if (b==1) fragColor = texelFetch(iChannel1,ivec2(fragCoord),0);
    if (b==2) fragColor = texelFetch(iChannel2,ivec2(fragCoord),0);
    if (b==3) fragColor = texelFetch(iChannel3,ivec2(fragCoord),0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //EarlyExit(iTime, 0);
    EarlyExitByFrame(iFrame, 0);
    vec2 uv = (fragCoord / iResolution.x) * 2.0 - 1.0;
    vec3 c = vec3(Bob(iTime, uv));
    fragColor = vec4(c,1);
}

