int getBufferByTime(float iTime)
{
    return int(mod(iTime*200.0,4.0));
}

int getBufferByFrame(int iFrame)
{
    return iFrame & 3;
}

void EarlyExit( float time, int buffer )
{
    if (getBufferByTime(time) != buffer) discard;
}

void EarlyExitByFrame( int frame, int buffer )
{
    if (getBufferByFrame(frame) != buffer) discard;
}

float Bob(float time, vec2 uv)
{
    float t = time;
    float c = smoothstep(0.05 + sin(time * 5.0) * 0.03, 0.003,length(uv + vec2(sin(t), cos(t) * sin(t*1.3)*2.0)));
    if (c==0.) discard;
    return pow(c, 0.35);
}
