float f(in float x){
    return 0.25*(pow(exp(x)+exp(-x),2.0)-pow(exp(x)-exp(-x),2.0)); 	// always equal to 1
}

float grad(in float x){
    const float eps = 1e-2;
    float a = (f(x+eps)-f(x-eps))/(2.0*eps);
    return sqrt(a*a+1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float s = 0.05*(sin(iTime)+2.0)*length(iResolution.xy);
    vec2 p = (fragCoord-0.5*iResolution.xy)/s+vec2(10.0*sin(0.2*iTime),1.0);
    float sd = 1.0-min(0.2*s*(abs(p.x)-0.008),1.0);
    vec3 col = vec3(0,0,sd);
    sd = min(s*(abs(p.y)-0.03),1.0);
    col = mix(vec3(1,0,0),col,sd);
	sd = (f(p.x)-p.y)/grad(p.x);
    sd = clamp(0.0,1.0,s*(abs(sd)-0.03));
    col = mix(vec3(0,1,0),col,sd);
    fragColor = vec4(col,1.0);
}
