#define N 12.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fract((fragCoord.xy)/iResolution.x*N);
    vec2 color = vec2(step(uv.y,2.0*(1.0-uv.x))*step(uv.y,2.0*uv.x));
    // Output to screen
    fragColor = vec4(color.xxx,1.0);
}
