void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float disp = 0.01 + 0.005 * sin((atan((uv.y-0.5)/(uv.x-0.5))+iTime*0.1) * 20.);
    float step_size = 0.01;
    vec2 d_out = (uv-0.5)/max(length(uv-0.5), smoothstep(-0.2, 0.2, sin(iTime))*0.6+0.7);
    float tr = texture(iChannel0, uv - (disp+1.*step_size)*d_out).r;
    float tg = texture(iChannel0, uv - (disp+2.*step_size)*d_out).g;
    float tb = texture(iChannel0, uv - (disp+3.*step_size)*d_out).b;
    vec3 col = vec3(tr, tg, tb);
    //col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
