const float pi = 3.141592653589793238462643383279502;

float hash13 (vec3 p3)
{
 	p3  = fract (p3 * 0.1031);
    p3 += dot (p3, p3.yzx + 33.33);
    return fract ((p3.x+p3.y)*p3.z);
}

vec2 ang2vec(float a)
{
 	return vec2(cos(a), sin(a));   
}

vec2 read(vec2 p) {
	return texture(iChannel0, p/iResolution.xy).xy;
}

vec2 avgpool(vec2 p) {
  vec2 acc = vec2(0.0);
  for (int y=-1; y<=1; ++y)   
  for (int x=-1; x<=1; ++x) {
    acc += read(p + vec2(x, y));
  }
  return acc / 9.0;
}

