#define PI 3.1415926
#define N 25.0

vec3 palette(float t)
{
    //return 0.5 + 0.5 * cos(vec3(t + 0.0/3.0, t + 1.0/3.0, t + 2.0/3.0) * 2.0 * PI);
    return 0.5 + 0.5 * cos(vec3(t + 2.0/3.0, t + 1.0/3.0, t + 0.0/3.0) * 2.0 * PI);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    //vec3 random = texture(iChannel0, vec2(iTime, 0.5)).xyz;

    float h = 0.0;
    for(float i = 1.0; i <= N; i++) 
        for(float j = 1.0; j <= N; j++)
        {
            vec3 random = texture(iChannel0, vec3(i/N, j/N, iTime / 200.0)).xyz;
            h += sin(j * (random.x + uv.x) * 2.0 * PI) * sin(i * (random.y + uv.y) * 2.0 * PI);
        }

    h /= N * N;

    //fragColor = vec4(palette(h), 1.0);
    fragColor = vec4(palette(pow(10.0 * h, 3.0)), 1.0);
}
