   #define ease(v) atan( (v) * t ) / atan(v) 
// #define ease(v) sign(t)* pow( abs(t), 1./(v) ) 

   #define A(v) clamp(  1.5 - R.y/3.*( v ) , 0., 1. )
// #define A(v) clamp(  1.5 - (v)/fwidth(v) , 0., 1. )

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = 3.* u / R.y;
    float i = round(U.x),
          t = sin(6.28*iTime);
    O = vec4( A( length( vec2(i, 1.5+ ease(i+1.) ) - U ) -.1 ) );

    t = sin(6.28*(-iTime-fract(U.x+.5)));
    O.r += .5* A( abs( 1.5+ease(i+1.)- U.y )  );
}
