// My reflect function
vec3 myReflect(vec3 inDirection, vec3 normal) 
{
    // Whether this should be normalized ?
    return inDirection - 2.*dot(inDirection, normal) * normal; // see https://raytracing.github.io/books/RayTracingInOneWeekend.html#diffusematerials 9.4
}

// 1 is lambertian; 2 is metal; 3 is dielectrics
bool materialScatter(HitInfo info, Ray rayIn, out Ray rayOut, out vec3 attenuation)  // return whether scatter; store new ray info
{
    int hitMaterial = info.hitMaterial;
    if (hitMaterial == 1){
        rayOut.origin = info.p;
        vec3 target = info.p + info.normal + random_in_unit_sphere();
        rayOut.direction = target - info.p;
        attenuation = info.hitAttenuation;
        return true;
    } else if (hitMaterial == 2) {
        rayOut.origin = info.p;
        rayOut.direction = reflect(rayIn.direction, info.normal) + info.hitFuzzy * random_in_unit_sphere();
        attenuation = info.hitAttenuation;
        return (dot(rayOut.direction, info.normal) > 0.);
    }
    return false;
}

// Check whether the ray hit the sphere and store all the hit info
bool isHitSphere(Sphere s, Ray ray, float tMin, float tMax, out HitInfo info)
{   
    bool isHit = false;
	vec3 aMinusC = ray.origin - s.center;
    float a = dot(ray.direction, ray.direction);
    // float a = sqrt(length(ray.direction));
    float bhalf = dot(ray.direction, aMinusC);
    float b = 2. * bhalf;
    float c = dot(aMinusC, aMinusC) - s.radius * s.radius;
    float discriminant = b*b - 4.*a*c;
    if (discriminant < 0.) return false;
    float t = (-b - sqrt(discriminant)) / (2.*a);
    if (t < tMin || t > tMax) {    // Check the min and max range of the ray
        t = (-b + sqrt(discriminant)) / (2.*a);
        if (t < tMin || t > tMax) return isHit;
    }

    // Set hit info
    isHit = true;
    info.t = t;
    info.p = ray.origin + t*ray.direction;
    info.normal = (info.p - s.center) / s.radius;
    info.hitMaterial = s.sphereMaterial;
    info.hitAttenuation = s.albedo;
    info.hitFuzzy = s.fuzz;
    //info.normal = vec3(1., 0., 0.);
    return isHit;
}

