struct Camera
{
	vec3 origin;
    vec3 lookAt;
    vec3 f; // forward
    vec3 u;  // right
    vec3 v;  // up
    float zoom;
};

Camera camera(vec3 origin, vec3 lookAt, float zoom)
{
    Camera camera;
    camera.origin = origin;
    camera.lookAt = lookAt;
    camera.f = normalize(camera.lookAt - camera.origin);
    camera.u = cross(vec3(0., 1., 0.), camera.f);
    camera.v = cross(camera.f, camera.u);
    camera.zoom = zoom;
    return camera;
}

Ray cameraGetRay(Camera camera, float u, float v) 
{
    vec3 origin = camera.origin;
    vec3 direction = camera.origin + camera.zoom * camera.f + u * camera.u + v * camera.v -camera.origin;
    return ray(origin, direction);
}



