        	fragCoord -= iResolution.xy / SCALE_RATIO;
        } else {
			discard;
        }
	}
	vec2 logCoord = logMap(SCALE_RATIO, fragCoord, iResolution.xy);

	mainShader(fragColor, logCoord);
}
