//#define LIGHTING

float sampleHeightmap(in vec2 p) {
    return dot(texture(iChannel0, p / iResolution.xy).rgb, vec3(1.0)) * 10.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 a = vec3(fragCoord.x + 1.0, sampleHeightmap(fragCoord + vec2(1.0, 0.0)), fragCoord.y);
    vec3 b = vec3(fragCoord.x, sampleHeightmap(fragCoord), fragCoord.y);
    vec3 c = vec3(fragCoord.x, sampleHeightmap(fragCoord + vec2(0.0, 1.0)), fragCoord.y + 1.0);
    vec3 n = normalize(cross(c - b, a - b));

    #ifdef LIGHTING
    vec3 l = vec3(-0.58, 0.58, 0.58);
    n = vec3(max(0.0, dot(n, l)));
    #endif

    fragColor = vec4(n, 1.0);
}
