void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = fragCoord.xy;
    int bigx = int(xy.x)/4;
    int bigy = int(xy.y)/4;


    bool isb = (bigx+bigy)%2==0;
    if(isb){
    fragColor = vec4(0.4,0.4,0.4,1.0);
    }else{
     fragColor = vec4(0.6,0.6,0.6,1.0);   
    }
}
