void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float l;
    vec2 uv;
    vec2 p = fragCoord.xy/iResolution.xy;
    uv=p;
    p+=5.;
    l=length(uv);
    uv+=p*(sin(iTime/5.0))*abs(sin(l * 20.));
    vec3 color=vec3(0.02/length(mod(uv,1.)-.5));

	fragColor=vec4(color,1.);
}
