#define SPEED 1.0

#define TOP_COLOR vec3(.082,.09,.11)
#define BOTTOM_COLOR vec3(.796,.204,.122)
//#define TOP_COLOR vec3(.0,.0,.0)
//#define BOTTOM_COLOR vec3(1.,1.,1.)


vec3 random3(vec3 c) {
    float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
    vec3 r;
    r.z = fract(512.0*j);
    j *= .125;
    r.x = fract(512.0*j);
    j *= .125;
    r.y = fract(512.0*j);
    return r-0.5;
}

// snoise function author @patriciogv ( patriciogonzalezvivo.com ) - 2015
const float F3 =  0.3333333;
const float G3 =  0.1666667;
float snoise(vec3 p) {

    vec3 s = floor(p + dot(p, vec3(F3)));
    vec3 x = p - s + dot(s, vec3(G3));

    vec3 e = step(vec3(0.0), x - x.yzx);
    vec3 i1 = e*(1.0 - e.zxy);
    vec3 i2 = 1.0 - e.zxy*(1.0 - e);

    vec3 x1 = x - i1 + G3;
    vec3 x2 = x - i2 + 2.0*G3;
    vec3 x3 = x - 1.0 + 3.0*G3;

    vec4 w, d;

    w.x = dot(x, x);
    w.y = dot(x1, x1);
    w.z = dot(x2, x2);
    w.w = dot(x3, x3);

    w = max(0.6 - w, 0.0);

    d.x = dot(random3(s), x);
    d.y = dot(random3(s + i1), x1);
    d.z = dot(random3(s + i2), x2);
    d.w = dot(random3(s + 1.0), x3);

    w *= w;
    w *= w;
    d *= w;

    return dot(d, vec4(52.0));
}

float grad(vec2 uv)
{
	return uv.y + sin(.66 * uv.x * uv.x * cos(2. * uv.x) * 30.6) / 40. + snoise(vec3((uv * 20.).xy, SPEED * iTime)) / 20.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;

	vec3 col = mix(BOTTOM_COLOR, TOP_COLOR, grad(uv));

	fragColor = vec4(col,1.0);
}
