#define GOLDEN_ANGLE 2.39996

#define ITERATIONS 144

mat2 rot = mat2(cos(GOLDEN_ANGLE), sin(GOLDEN_ANGLE), -sin(GOLDEN_ANGLE), cos(GOLDEN_ANGLE));

vec3 Bokeh(sampler2D tex, vec2 uv, float radius)
{
	vec3 acc = vec3(0), div = acc;
    float r = 1.;
    vec2 vangle = vec2(0.0,radius*.05 / sqrt(float(ITERATIONS)));

	for (int j = 0; j < ITERATIONS; j++)
    {  
        r += 1. / r;
	    vangle = rot * vangle;
        vec3 col = texture(tex, uv + (r-1.) * vangle).xyz;
		vec3 bokeh = pow(col, vec3(5.));
		acc += col * bokeh;
		div += bokeh;
	}
	return acc / div;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float t = (sin(iTime*10.)*0.5+0.5);
    float m = clamp(length(pow(texture(iChannel1,uv).rgb,vec3(3.))),0.,1.);
    if ( iMouse.w > 0.5 ){
        m*=iMouse.x/iResolution.x;
    }
    fragColor = vec4(Bokeh(iChannel0, uv, t*m), 1.0);
}
