void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - .5*iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy/iResolution.xy)*4. - 2.;

    vec2 c = (uv*2.)/pow(10.,iTime*0.1) + m;
    vec2 z = vec2(0.);

    float iteration = 0.;
    const float max_iterations = 150.;

    for(float i=0.;i<max_iterations;i++){

     z = vec2(pow(z.x, 2.) - pow(z.y, 2.), (2. * z.x * z.y)) + c;
     if(length(z) > 2.)
         break;
     iteration++;
    }

    float f = iteration/max_iterations;
    vec3 col = vec3(f);
    fragColor = vec4(col,1.0);
}
