#define _SIZE 0.5

float diamond( vec2 p, float r )
{
   return 2.0*r - (abs(p.x) + abs (p.y)) ;
}

float square( vec2 p, float r )
{
   return 2.0*r - max(abs(p.x), abs(p.y));
}

float fourPintStar( vec2 p, float r )
{
   return (r - abs(abs(p.x) - abs(p.y))) + (r - max(abs(p.x), abs(p.y))) * 0.5;
}

float fourPintStarMaskUpper( vec2 p)
{
   return step(mod(atan(p.y, p.x) / 3.14159 * 4.0, 2.0), 1.0);
}

float fourPintStarMaskLower( vec2 p)
{
   return step(1.0 - mod(atan(p.y, p.x) / 3.14159 * 4.0, 2.0), 0.0);
}

vec2 opRep( vec2 p, vec2 c )
{
    return mod(p,c)-0.5*c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x/ iResolution.y;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 cUV = -1.0+2.0*uv;
    cUV.x *= ratio;

    float star = fourPintStar(cUV, _SIZE);
    vec3 starUpper = smoothstep(_SIZE, _SIZE + 0.0001, star * fourPintStarMaskUpper(cUV)) * vec3(0.54, 0.54, 0.58);
    vec3 starLower = smoothstep(_SIZE, _SIZE + 0.0001, star * fourPintStarMaskLower(cUV)) * vec3(0.79, 0.81, 0.81);
    vec3 starcol =  starLower + starUpper ;
    vec3 diamondCol = smoothstep(_SIZE, _SIZE + 0.0001, diamond(cUV, _SIZE)) * vec3(1.0, 0.99, 0.91);

    vec3 col = smoothstep(_SIZE, _SIZE + 0.0001, square(cUV, _SIZE)) * vec3(0.99, 0.93, 0.79);
    col = mix( col, diamondCol, step( 0.1, length(diamondCol)) );
    col = mix( col, starcol, step( 0.1, length(starcol)) );

    // Output to screen
    fragColor = vec4(col, 1.0);
}
