float dist(vec2 origin, vec2 target)
{
    return abs(sqrt(pow(target.x+origin.x,2.)+pow(target.y+origin.y,2.)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //pixel coord center origo
    vec2 uv = fragCoord/iResolution.xy;
    uv-=vec2(.5);

    //set aspect ratio
    float ratio=iResolution.x/iResolution.y;
    uv.x*=ratio;

    //set time
    float t = iTime/4.;

    //save origo
    vec2 o=uv;

    //Fisheye anim
    //uv.x+=sin(t*6.)/6.;
    //uv.y+=sin(t*2.)/12.;

    //Fisheye
    float d=length(uv*2.);
    float z = sqrt(1.0 - d * d);
    float rad = atan(d, z) / 3.14159;
    float phi = atan(uv.y, uv.x);
    uv = vec2(rad*cos(phi)+.5,rad*sin(phi)+.5);
    uv-=vec2(.5);

	//circles
    float distFromCenter = dist(o,uv);
    float circles = sin(distFromCenter*10.);

    //recalculate center distance
    uv.x+=sin(-t*6.)/6.;
    uv.y-=cos(-t*3.)/6.;
    distFromCenter = dist(vec2(0.),uv);
    float mid = 1.+sin(distFromCenter)*2.;

    //second circles
    //uv.x*=mid*3.;
    //uv.y*=mid*3.;
    distFromCenter = dist(vec2(0.),o);
    float circles2 = sin(distFromCenter*mid*160.);

    vec3 col = vec3(circles*mid-circles2/mid);
    //vec3 col = vec3(circles - circles2*mid);




    //Output to screen
    fragColor = vec4(col,1.0);
}
