#define PI 3.141592

float clock(float factor) { return floor(iTime*factor) + ceil(iTime*factor) ; }
float softClock(float factor) { return clock(factor) - pow(fract(iTime),1./factor);}

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
mat2 r(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
		float factor = 2.+ floor(sin(iTime/10.))/2.;
        vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
        vec2 uvv =uv;
        uv*=4.+sin(softClock(factor)*4.+PI/4.);
        uv =abs(uv*r(softClock(factor)+floor(uv.x/.50*length(uvv))+floor(uv.y/.50*length(uvv))));
            float d = floor(uv.x) >= floor(uv.y) ? fract(abs(uv.x)) : fract(abs(uv.y)) ;
            vec3 col = mix(vec3(.1),vec3(.7*fract(uv.x)*.5,.9*fract(uv.y),.2+cos(clock(factor) + length(uvv*10.))/2.),vec3(d));
    fragColor = vec4(col,1.0);
}
