vec2 hash( vec2 x ){
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float gradientNoise(vec2 p){
	vec2 i = floor(p);
    vec2 f = fract(p);


    vec2 k = vec2(0,1);

    vec2 g1 = hash(i+k.xx);
    vec2 g2 = hash(i+k.xy);
    vec2 g3 = hash(i+k.yx);
    vec2 g4 = hash(i+k.yy);

    //vec2 s = f;
    //vec2 s = f*f*(3. - 2.*f);
    vec2 s = f*f*f*(f*(f*6.0-15.0)+10.0);

    float d1 = dot(f-k.xx,g1);
    float d2 = dot(f-k.xy,g2);
    float d3 = dot(f-k.yx,g3);
    float d4 = dot(f-k.yy,g4);


    return mix(mix(d1,d3,s.x),mix(d2,d4,s.x),s.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y*3.;

    fragColor = vec4(gradientNoise(uv))*.5 + .5;
}
