void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cv = (fragCoord.xy-.5*iResolution.xy) / iResolution.y;
    float d = length(cv);
    float t = iTime*.05;
    float n = pow(d*5.0, 5.+.1*sin(t));
    float pct = fract(n);
    vec3 col = texture(iChannel0, vec2(.5, pct)).rgb;
    col -= pct;
    fragColor = vec4(col,1.0);
}
