void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t = iTime/2.;
    uv = uv * mat2(cos(t),-sin(t),sin(t),cos(t));
    uv = fract(uv * .5*sin(-iTime)*4.)-.5;
    float a = atan(uv.y,uv.x);
    a = mod((a+3.141)/6.282,.1)*10.;
    a -= step(.4+sin((uv.y+uv.x)*88.)*.2,length(uv));
    uv = fract(uv * .5*cos(iTime-1.)*8.)-.5;
    float b = mod((a+3.141)/6.282,.1)*10.;
    b -= step(.4+sin((uv.y+uv.x)*88.)*.2,length(uv));
    vec3 col = vec3(a*2.,0.,5.);
    col = mix(col,vec3(0.,b*2.,.3),.5);

    fragColor = vec4(col,1.0);
}
