#define SPEED 50.0


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(cos(fragCoord.y/10.0+iTime*SPEED),cos(fragCoord.x/10.0+iTime*SPEED),cos((fragCoord.y*.05)+(fragCoord.x*.05)+iTime*SPEED));
    //vec3 col = vec3(0.0,0.0,cos((fragCoord.y*ECART)+(fragCoord.x*ECART)+iTime*SPEED));

    // Output to screen
    fragColor = vec4(col,1.0);
}
