const float D_MAX = 5.0;
const float f = 1.0 / 60.0 / 60.0 / 12.0;
const float R_VOID = 60.0;

float dist_to(float t, vec2 xy0, float r_interest){
    float theta = t * f * r_interest;
    vec2 xy = (r_interest + R_VOID) * vec2(sin(theta), cos(theta));
    return length(xy-xy0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy0 = (fragCoord - iResolution.xy/2.0);
    float r_int = max(round(length(xy0)) - R_VOID, 1.0);
    float t = iDate.w * 2.0 * 3.141592653;
    float flout = D_MAX;

    for(float i = -D_MAX; i<=D_MAX; i++){
        float r = max(r_int+i, 1.0);
        float dist = dist_to(t, xy0, r);
        flout = min(flout, dist);
    }
    flout = clamp(D_MAX-flout, 0.0, 1.0);

    float dist1 = dist_to(t, xy0, 1.0);
    float flout1 = clamp(D_MAX-dist1, 0.0, 1.0);

    float dist2 = dist_to(t, xy0, 12.0);
    float flout2 = clamp(D_MAX-dist2, 0.0, 1.0);

    float dist3 = dist_to(t, xy0, 12.0*60.0);
    float flout3 = .9 * clamp(D_MAX-dist3, 0.0, 1.0);

    fragColor = vec4(flout-flout2-flout3, flout-flout1-flout3, flout-flout1-flout2, 1.0);
}
