float nsin( float b ){ return sin(b)*.5+.5; }
float pi = 3.1415926; 
float scale = 16.;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    float t = iTime*5.; 
    vec2 uv = fragCoord/iResolution.xy;

    vec4 R = vec4( floor( fragCoord / scale ) == floor( iMouse.xy / scale ) ); 
    fragColor = (1.-R)*texture(iChannel0, uv);
    fragColor+=R*vec4(nsin(t+pi/3.), nsin(t+pi), nsin(t-pi/3.), R.x); 

}
