//Two sources of waves
//Points A and B
//all values have to have a decimal point (float)

//point A
const float aX = 0.15;   //  X value of point A
const float aY = 0.0;    //  Y value of point A

//point B
const float bX = -0.15;  //  X value of point B
const float bY = 0.0;    //  Y value of point B

//wave properties
const float speed = 10.0;    //  how quickly waves propagate forwards
const float wavelength = 0.3;//  wavelength 
// ALL VALUES HAVE TO HAVE A DECIMAL POINT!!!!!

// red = peak
// green = trough


float getDis(float x, float y,float xA,float yA){
    return pow(
    (pow((x-xA),2.)+pow((y-yA),2.))
    ,0.5);
    }

float getHeight(float x, float y,float xA,float yA,float wl,float t){
    return sin((wl*t-6.28318530718*(getDis(x,y,xA,yA)/wl)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime*speed;
    float col = ((
    ((
    getHeight(uv.x,uv.y,aX,aY,wavelength,t)+
    getHeight(uv.x,uv.y,bX,bY,wavelength,t)
    +2.)/4.) // change from range -2 - 2 to range 0 - 1

    ));

    float red;
    float grn;
    if(col > 0.5){
        grn = 2.0 - (2.0 * col);
        red = 1.0;
    }
    if(col <= 0.5){
        grn = 1.0;
        red = (2.0*col);
    }
    fragColor = vec4(red,grn,0.0, 1.0);        

    if(getDis(uv.x,uv.y,aX,aY)<0.02){
        fragColor = vec4(1.,0.,1., 1.0);
    }
    if(getDis(uv.x,uv.y,bX,bY)<0.02){
        fragColor = vec4(1.,0.,1., 1.0);
    }

}
