void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
      // uv = vec2(iResolution.x / iResolution.y, 1.) - 2.0 * fragCoord / iResolution.y;
      // uv = vec2(1., 1.) - 2.0 * fragCoord / iResolution.xy;
	float r = length(uv); //distance(vec2(0., 0.), uv);

    fragColor = (1. - r) * vec4(1., 0., 0., 1.);
}
