float dist(vec2 p, vec2 x)
{
    vec2 a = x - p;
    return 1.0 / dot(a, a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 points[3] = vec2[3](
        0.5 * vec2(sin(iTime), cos(iTime)),
        vec2(0.75 + sin(iTime * 0.00012313), 0.5),
        0.65 * vec2(sin(iTime * 0.129837987), cos(iTime)) + vec2(0.75)
    );
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    float sum = 0.0;
    for(int i = 0; i < points.length(); i++) {
    	sum += dist(points[i], uv);
    }

    sum = sum / (sum + 1.0);

    fragColor = texture(iChannel0, vec2(sum, fract(iTime * 0.05)));
}
