#define M_PI 3.14159265358

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = vec2(0.1) + 0.8 * fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv) * 0.91;

    vec2 center = (iResolution.xy) / 2.;
    float t = M_PI / 180. * float(iFrame*2);
    vec2 foci = center + vec2(cos(1.2*t), sin(1.1-t)) * 10.;
    float r = iResolution.y / 20.;

    if (distance(fragCoord, foci) < r)
    	fragColor = vec4(1., 0.1, 0.2, 1);
}
