void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float fFrame = float(iFrame) / 100.;
    vec2 center = vec2(iResolution) / 2.;
    vec2 diff = vec2(cos(fFrame), sin(fFrame)) * iResolution.y / 2.2;
    vec2 pos1 = center - diff;
	vec2 pos2 = center + diff;

    fragColor = vec4(0, 0, 0, 1);
    if ((distance(fragCoord, pos1) + distance(fragCoord, pos2)) < distance(pos1, pos2) + 20.)
        fragColor.r = 1.;

    if (distance(fragCoord, pos1) < 3. || distance(fragCoord, pos2) < 3.)
        fragColor.bg = vec2(1.);
}
