const float pi = 3.14159;

float hash11(float n) {
    return fract(sin(n*434.4)*543.2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    uv *= 10.;
    uv.x += iTime * .5;
    vec3 col = vec3(0.);
    float s = sin(uv.x * 0.5);
    float f = floor(uv.y - 1. - s);

    for (float i = 0.; i < 2.5; i += 0.5) {
        float n = i + f + 0.5;
        float c = smoothstep(0.8, 0.5, abs(uv.y - n - s));
        col = col + (c * cos(uv.x*0.5 + hash11(n)*10.*iTime*vec3(.2,.5,.7)));
    }

    fragColor = vec4(col,1.0);
}
