{
    vec2 uv = fragCoord.xy /iResolution.xy;

    float a = uv[0];
    float b = uv[1];

    vec2 manifold = vec2(
        pow(a, 2.0) + 
        pow(b, 3.0), 
        pow(a, 2.0) - 
        pow(b, 3.0));
    //ceil
    vec2 manifold2 = vec2(
        sin(pow(manifold[0], 2.0) + pow(manifold[1], 2.0)), 
        cos(pow(manifold[0], 2.0) + pow(manifold[1], 2.0)) );

    vec2 sur_def = vec2(
    clamp(dFdx(manifold[0]/ manifold2[0])* iTime, .001, .002),
    clamp(dFdy(manifold[0]/ manifold2[1])* iTime, .001, .002) );


    float r = ceil( iTime + (manifold2[0]) + cos((manifold2[1])) );
    // scale = 5.15
    float x = grid( ( manifold2 - manifold  + sin(iTime*.02) ), 15.0, .15 );


    vec3 col = 0.6 + 0.3*cos(iTime+uv.xyx+vec3(0,8,16));
    // first pow = 1.2
