#define scale 6.

void mainImage( out vec4 fragColor, in vec2 pos )
{
    vec3 clr = vec3(0,0,0);
    vec2 pos2 = pos/scale;

    float time = abs(10. * sin(iTime/5.));

    clr.r = pow(cos(pos2.y + pow(sin(pos2.x + pow(time, time)), time)), time);//vertical lines
    clr.g = pow(sin(pos2.x + pow(cos(pos2.y + pow(time, time)), time)), time);//horizontal lines
    clr.b = 1.;

    fragColor = vec4(clr,1.0);
}
