vec3 pixelFromFreq(float freq /* x100 THz */ ) {
    return step(4.0, freq) * step(freq, 5.2) * vec3(1.0, (freq - 4.0)/1.2, 0.0)
        + step(5.2, freq) * step(freq, 5.65) * vec3(1.0 - (freq - 5.2)/0.45, 1.0, 0.0)
        + step(5.65, freq) * step(freq, 6.1) * vec3(0.0, 1.0, (freq - 5.65)/0.45)
        + step(6.1, freq) * step(freq, 6.45) * vec3(0.0, 1.0 - (freq - 6.1)/0.35, 1.0)
        + step(6.45, freq) * step(freq, 7.9) * vec3((freq - 6.45)/1.45, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float freq = 4.0 + 3.9 * uv.x;

    vec3 col = pixelFromFreq(freq);

    // Output to screen
    fragColor = vec4(col * 0.1 * (step(0.008, mod(freq, 0.5)) + 9.0), 1.0);
}
