void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord.xy/iResolution.xy);
}

const float SQRT32 = sqrt(3.0)/2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 oldcol = texture(iChannel0, (fragCoord.xy + vec2(-0.2 + 1.0*sin(0.1*fragCoord.y - 6.282*iTime), 0.2 + 1.5*sin(0.01*fragCoord.x + 0.2*iTime)))/iResolution.xy).rgb;
    vec3 newcol = mix(vec3(0.5, 0.5, 0.5) + cos(iTime/2.0)*vec3(0.5, -0.25, -0.25) + sin(iTime/2.0)*vec3(0.0, 0.5, -0.5)*SQRT32, vec3(1.0, 1.0, 1.0), smoothstep(10.5, 11.5, distance(iMouse.xy, fragCoord.xy)));

    fragColor = vec4(mix(oldcol, newcol, 0.001)*newcol,1.0);
}
