void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    //.x
    //.15 up
    //.25 right
    //.35 down
    //.45 left
    //.y
    //.15 came from a white
    //.25 came from a black
    //same as usual uv but we offset by one so we grab the previous frame/texture from Buf A one frame higher;
    vec2 ouvb = vec2(fragCoord.x, fragCoord.y-1.0) / iResolution.xy;
    vec2 ouva = vec2(fragCoord.x, fragCoord.y+1.0) / iResolution.xy;
    vec2 ouvl = vec2(fragCoord.x-1.0, fragCoord.y) / iResolution.xy;
    vec2 ouvr = vec2(fragCoord.x+1.0, fragCoord.y) / iResolution.xy;
    vec2 ouv = vec2(fragCoord.x, fragCoord.y) / iResolution.xy;
    //grab previous frame but offset by one pixel
    fragColor = texture(iChannel0, ouv);
    vec4 fragColorb = texture(iChannel0, ouvb);
    vec4 fragColora = texture(iChannel0, ouva);
    vec4 fragColorl = texture(iChannel0, ouvl);
    vec4 fragColorr = texture(iChannel0, ouvr);
    if (fragColor.y > 0.1 && fragColor.y < 0.2) {
    	fragColor = vec4(1.0,1.0,1.0,1.0);
    } else if (fragColor.y > 0.2 && fragColor.y < 0.3) {
    	fragColor = vec4(0.0,0.0,0.0,0.0);
    } 
    if (fragColorb.x > 0.1 && fragColorb.x < 0.2) {
        if (fragColor.x < 0.2) {
        	fragColor = vec4(0.25,0.15,0.0,0.0);
        } else {
            fragColor = vec4(0.45,0.25,0.0,0.0);
        }
    } else if (fragColora.x > 0.3 && fragColora.x < 0.4) {
        if (fragColor.x < 0.2) {
        	fragColor = vec4(0.45,0.15,0.0,0.0);
        } else {
            fragColor = vec4(0.25,0.25,0.0,0.0);
        }
    } else if (fragColorl.x > 0.2 && fragColorl.x < 0.3) {
        if (fragColor.x < 0.2) {
        	fragColor = vec4(0.35,0.15,0.0,0.0);
        } else {
            fragColor = vec4(0.15,0.25,0.0,0.0);
        }
    } else if (fragColorr.x > 0.4 && fragColorr.x < 0.5) {
        if (fragColor.x < 0.2) {
        	fragColor = vec4(0.15,0.15,0.0,0.0);
        } else {
            fragColor = vec4(0.35,0.25,0.0,0.0);
        }
    }
	//fragColor = vec4(.5,1.0,1.0,1.0);
    if (iFrame%150<1)
    {
        //randomize positions on first frame
    	fragColor = texture(iChannel1, ouv);
        fragColor = vec4(vec3(floor(fragColor.y+0.5)),1.0);
        if (mod(fragCoord.x,5.0)<1.0 && mod(fragCoord.y,5.0)<1.0) {
        	fragColor = vec4(.15,.15,.5,1.0);
        }
    }

}

