#define VO void
#define B1 bool
#define I1 int
#define F1 float
#define F2 vec2
#define F3 vec3
#define F4 vec4
#define _01 F2(0,1)
#define _0  _01.xxx

// some globals
F4 O,   // output color
   R;   // resolution (xy) and its inverse (zw)

F2 N,   // (0,0) bis (1,1)
   M;

VO init( F2 i ) {

    R.xy = iResolution.xy;
    R.zw = 1. / R.xy;

    N = i * R.zw;

    M = iMouse.xy * R.zw;

    if( M.x <= 0. || 1. < M.x ) M.x = .5;
    if( M.y <= 0. || 1. < M.y ) M.y = .5;


    O = texture( iChannel0, N );
}

VO work( ) {

    F1 parts = 1. + floor( 128. * M.y );

    F2 Nn = floor( parts * N ) / parts;

    F3 cl = texture( iChannel0, Nn + .5 / parts ).rgb;

    F2 Nr = N - Nn;

    F1 f = 1. / ( 30. * parts ), p = parts * 1.2;

    O.rgb =
        5. * f < Nr.x  &&  Nr.x < 9. * f
        	? Nr.y * p < cl.r
        		? F3( N.x < M.x ? 1. : cl.r, 0, 0 )
        		: _0
            : 13. * f < Nr.x  &&  Nr.x < 17. * f
                ? Nr.y * p < cl.g
            	    ? F3( 0, N.x < M.x ? 1. : cl.g, 0 )
                	: _0
                    	: 21. * f < Nr.x && Nr.x < 25. * f
				? Nr.y * p < cl.b
					? F3( 0, 0, N.x < M.x ? 1. : cl.b )
	                : _0
				: _0;

}

VO finish( out F4 o ) {

    o = F4( clamp( O.rgb / O.a, 0., 1. ), 1. );
}

VO mainImage( out F4 o, in F2 i ) {

    init( i );

    work( );

    finish( o );        
}
