// Code by Flopine

// Thanks to wsmind, leon, XT95, lsdlive, lamogui, 
// Coyhot, Alkama,YX, NuSan and slerpy for teaching me

// Thanks LJ for giving me the spark :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and other to sprout :)  https://twitter.com/CookieDemoparty


#define TAU 6.2831853

#define time(speed) fract(iTime*speed)

#define AAstep(thre, val) smoothstep(-.7,.7,(val-thre)/min(0.07,fwidth(val-thre)))
#define square(puv,s) (max(abs(puv.x),abs(puv.y))-s)

#define xor(a,b) ((1.-b)*a+(1.-a)*b)

float frame (vec2 uv)
{
    float size = 0.1;
    float thin = 0.03;
    float mask = 1.;
    vec2 uu = uv;
    for (int i=0; i<6; i++)
    {
        float ratio = float(i)/6.;
        float a = time(0.18+ratio)*TAU;
        uu += vec2(cos(a),sin(a))*0.05;
        uu *= vec2(0.96,1.);
        mask *= AAstep(thin,abs(square(uu,size)));        
        size += 0.22;
        thin += 0.01;
    }

    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col = vec3(frame(uv+0.03),
                    frame(uv-0.02),
                    frame(uv-0.05)
                   );
    col = xor(col,vec3(0.,0.,0.15));
    fragColor = vec4(sqrt(col),1.0);
}
