float pcurve( float x, float a, float b )
{
    float k = pow(a+b,a+b) / (pow(a,a)*pow(b,b));
    return k * pow( x, a ) * pow( 1.0-x, b );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv.x = uv.x - 0.5;
    uv.y=uv.y- 0.5;
    uv.x = uv.x*iResolution.x/iResolution.y;

    vec2 Pos = vec2 (0.7,-0.1*1.5*sin(iTime));// position of the sun 
    float d = length ( uv - Pos);
    float radius = 0.05;
    float col = smoothstep(radius, radius-0.01, d);


vec3 color = vec3(1.,sin(iTime+3.14)*(1.8-col),1.*(1.1-col));
    vec3 blue = vec3 (0.5,0.5,1.);
    if (uv.y > -0.2)
    {
        blue = blue + vec3(uv.y,uv.y,0.);
    }
    else
        blue = blue - vec3(0.9+uv.y*2.,0.9+uv.y*2., 0.);
    vec3 last = mix(color,blue,1.-col);


vec3 colorA = vec3(0.5,0.0,0.0);
vec3 grad = (1.1-d)*1.3* colorA;    
vec3 lastLast= mix(last,grad, 0.8*(2.-col)*(-Pos.y+0.2));
    fragColor = vec4(lastLast,1.0);     
}
