#define time iTime
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 color = vec3(0.0);

    uv.x +=sin(uv.y*0.1);
    float q = 0.;
    if (sign(sin(uv.y*10.+4.*sin( (floor(time) + pow(fract(time),2.)) ))) < 0.) {
        uv.x+=.03+time/8.;;
        q=1.;
    } else {
        uv.x -= time/4.;
    }
    float d = smoothstep(0.40,0.39,cos(sin(uv.x*20.+time)+.5*.5));

    color = mix(vec3(q,0.,q/2.),vec3(0.,1.-q,1.-q),d);
    fragColor = vec4(
       color,
        1.0);
}
